package day4;

public class PositiveornegativeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=0;
		if(num>0)
		{
			System.out.println("positive number");
		}
		else if(num<0)
		{
			System.out.println("negative number");
		}
		else
		{
			System.out.println("zero");
		}

	}

}
