package day5;

public class DowhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//1----10 numbers
		int i=1;
		do {
			System.out.println(i);
			i++;
		}
		while(i<=10);
		System.out.println("-----------------------");
		
		//10,9,8,7-------1
		int k=10;
		do
		{
			System.out.println(k);
			k--;
		}
		while(k>=1);
		System.out.println("-----------------------");
		//1------10 numbers
		int z=1;
		do 
		{
			System.out.println(z); 
			z++;
		}
		while(z<=10);
		System.out.println("-----------------------");
		
		
	}

}
